            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(json_data),
        });
        const data = await res.json();
        //crear condicion
        //window.location.href = "http://localhost:3000/login";
    };


    return (
        <div className="login-page" style={{height: '100vh'}} >  
            {/**HEADER */} 
            <header className="header header-scrolled fixed-top d-flex align-items-center header-transparent">
                <div className="container d-flex justify-content-between align-items-center">
                    <div className="logo col-md-6">
                        <h1 className="text-light">
                            <Link to="/#" ><span>Wallet</span></Link>
                        </h1>
                        {/**ARREGLAR LOGO POR SI SE NECESITA */}
                        {/*<Link to="/"><img src={Logo} className="img-fluid" alt="" /></Link>*/}

                    </div>
                    <nav className="navbar-global">
                            <ul>
                                <li><input className="form-control" placeholder="Correo Electronico" type="email" onChange={(e) => setEmail(e.target.value)}></input></li>
                                <li><input className="form-control" placeholder="ContraseÃ±a" type="password" onChange={(e) => setPassword(e.target.value)}></input></li>
                                <li><button type="button" className="btn btn-outline-light" onClick={handleSubmit}>Iniciar sesiÃ³n</button></li>
                            </ul>
                            {/*<i className="bi bi-list mobile-nav-toggle"></i>*/}
                    </nav>
                </div>
            </header>
            <section className="section section-shaped section-lg">
                <div className="shape shape-style-1 bg-gradient-default">
                    <span />
